// App.js
import React, { useState } from 'react';
import ColorPalette from './ColorPalette';
import src_notify from './src_notify.mp3';
import paletteData from './pallete.json';

const App = () => {
  const [selectedPalette, setSelectedPalette] = useState(null);
  const [selectedColor, setSelectedColor] = useState('');

  const handlePaletteSelect = (palette) => {
    setSelectedPalette(palette);
  };

  const handleColorSelect = (color) => {
    navigator.clipboard.writeText(color);
    const audio = new Audio(src_notify);
    audio.play();
    setSelectedColor(color);
  };

  return (
    <div className="app">
      <header className="header">
        <h1>Flat UI colors</h1>
      </header>

      <main className="main">
        {selectedPalette ? (
          <ColorPalette palette={selectedPalette} onColorSelect={handleColorSelect} />
        ) : (
          paletteData.map((palette, index) => (
            <button key={index} onClick={() => handlePaletteSelect(palette)}>
              Select Palette {index + 1}
            </button>
          ))
        )}
      </main>

      <footer className="footer">
        <p>Shanaev A. Young creator who likes programming and plays D&D with "reality" friend</p>
        {selectedColor && <p>Selected Color: {selectedColor}</p>}
      </footer>
    </div>
  );
};

export default App;
